import { Component, Injectable } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormBuilder } from '@angular/forms';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-registration-page',
  templateUrl: './registration-page.component.html',
  styleUrls: ['./registration-page.component.scss']
})


export class RegistrationPageComponent {
  title = 'Registration';
  public registrationForm !: FormGroup

  constructor(private formBuilder: FormBuilder, private http: HttpClient, private route: Router) { }

  //constructor(private route: Router){ }

  ngOnInit(): void {
    this.registrationForm = this.formBuilder.group({
      userName: [""],
      password: [""],
      email: [""]
    })
  }

  register(){
    this.http.post<any>("http://localhost:3000/signupUsersList",this.registrationForm.value)
    .subscribe(res=>{
      alert('SIGNIN SUCCESFUL');
      this.registrationForm.reset()
      this.route.navigate(["Home"])
    },err=>{
      alert("Something went wrong")
    })
  }

  

  navigateToHomePage(){
    this.route.navigate(['Home'])
  }
}
<html>
<head>
    <title>Registration Form</title>
</head>
<body>
    <h2>TUNEBOX REGISTRATION</h2><br>
    <div class="registration">
    <form [formGroup]= "registrationForm" (ngSubmit)="register()" >
        <label><b>Username
        </b>
        </label>
        <input formControlName="userName" type="text" name="Uname" id="Uname" placeholder="Username" required>
        <br><br>
        <label><b>Password
        </b>
        </label>
        <input formControlName="paswword"type="password" name="Pass" id="Pass" placeholder="Password">
        <br><br>
        <label><b>Email
        </b>
        </label>
        <input formControlName="email" type="email" name="Email" id="email" placeholder="Email">
        <br><br>
        <button>Register</button>
        <a (click)="navigateToHomePage()" class="button" name="log" id="log"> Login </a>
        <br><br>
        
       
    </form>
</div>
</body>
</html>
